<script>
    document.querySelectorAll('.edit-report-btn').forEach(button => {
        button.addEventListener('click', function() {
            const reportId = this.dataset.id;
            const reportTitle = this.dataset.title;
            const reportDescription = this.dataset.description;
            const reportUser = this.dataset.user;
            const reportType = this.dataset.type;
            // Populate modal fields
            document.getElementById('editReportId').value = reportId;
            document.getElementById('editReportTitle').value = reportTitle;
            document.getElementById('editReportDescription').value = reportDescription;
            document.getElementById('editReportUser').value = reportUser;
            document.getElementById('editReportType').value = reportType;

            // Update form action
            document.getElementById('edit-report-form').action = `/report/update/${reportId}`;
            // Show modal
            document.getElementById('editReportModal').classList.remove('hidden');
        });
    });

    document.querySelectorAll('.delete-report-btn').forEach(button => {
        button.addEventListener('click', function() {
            const reportId = this.dataset.id;

            // Update form action
            const form = document.getElementById('delete-report-form');
            form.action = `/report/delete/${reportId}`;

            // Show modal
            document.getElementById('deleteReportModal').classList.remove('hidden');
        });
    });


    document.querySelectorAll('.delete-report-btn').forEach(button => {
        button.addEventListener('click', function() {
            // Get the report ID from the button's data attributes
            const reportId = this.dataset.id;

            // Set the report ID in the hidden input field
            document.getElementById('deleteReportId').value = reportId;

            // Update the form action dynamically
            const form = document.getElementById('delete-report-form');
            form.action = `/report/delete/${reportId}`;

            // Show the delete modal
            document.getElementById('deleteReportModal').classList.remove('hidden');
        });
    });

</script>
